using ConaviWeb.Commons;
using ConaviWeb.Data.RH;
using ConaviWeb.Model.Request;
using ConaviWeb.Model.Response;
using ConaviWeb.Model.RH;
using ConaviWeb.Services;
using Microsoft.AspNetCore.Mvc;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Threading.Tasks;
using static ConaviWeb.Models.AlertsViewModel;

namespace ConaviWeb.Controllers.RH
{
    public class ViaticosSolicitudController : Controller
    {
        private readonly IRHRepository _rHRepository;
        private readonly IMailService _mailService;
        public ViaticosSolicitudController(IRHRepository rHRepository,IMailService mailService)
        {
            _rHRepository = rHRepository;
            _mailService = mailService;
        }
        public IActionResult Index()
        {
            var user = HttpContext.Session.GetObject<UserResponse>("ComplexObject");
            TempData["nombre"] = user.Name;
            ViewBag.Nombre = TempData["nombre"];
            TempData["puesto"] = user.Cargo;
            ViewBag.Cargo = TempData["puesto"];
            TempData["area"] = user.Area;
            ViewBag.Area = TempData["area"];
            TempData["cvNivel"] = user.CvNivel;
            ViewBag.CvNivel = TempData["cvNivel"];

            if (TempData.ContainsKey("Alert"))
                ViewBag.Alert = TempData["Alert"].ToString();
            return View("../RH/ViaticosSolicitud");
        }
        [HttpPost]
        public async Task<IActionResult> GuardarSolicitudAsync([FromBody] Viaticos viaticos)
        {
            var user = HttpContext.Session.GetObject<UserResponse>("ComplexObject");
            viaticos.IdUsuario = user.Id;
            viaticos.Folio = viaticos.IdUsuario + DateTime.Now.ToString("MMddyyyyHHmmss");
            viaticos.Periodo_comision_i = Convert.ToDateTime(viaticos.Periodo_comision_i).ToString("yyyy-MM-dd");
            viaticos.Periodo_comision_f = Convert.ToDateTime(viaticos.Periodo_comision_f).ToString("yyyy-MM-dd");
            viaticos.Fecha_salida = Convert.ToDateTime(viaticos.Fecha_salida).ToString("yyyy-MM-dd");
            viaticos.Fecha_regreso = Convert.ToDateTime(viaticos.Fecha_regreso).ToString("yyyy-MM-dd");

            var success = await _rHRepository.InsertViaticos(viaticos);
            if (!success)
            {
                var alertJson = AlertService.ShowAlert(Alerts.Danger, "Ocurrio un error al registrar la solicitud de viaticos");
                return Ok(alertJson);
            }
            var mail = new System.Text.StringBuilder();
            mail.Append("<!DOCTYPE html><html><style>table {border:2px solid #b8925f;border-radius:10px;}</style> ");
            mail.Append("<body><span style='text-align:center;display:block;margin-top:5px'> ");
            mail.Append("<img width='700' src='data:image/png;base64, ' /> ");
            mail.Append("</span> <br><br> <table style='width:75%;'> <tr style='margin-bottom:12px;font-size:18px;'>    <td><b>Buen día,</b></td>  </tr> ");
            mail.Append("<tr style='text-align:justify; height: 150px;'><td> ");
            mail.Append("Se ha registrado una nueva Solicitud de Viáticos  con el Folio: " + viaticos.Folio + ", puede ingresar en el siguiente link para dar seguimiento a la solicitud: ");
            mail.Append("https://sistemaintegraloperativo.conavi.gob.mx:9090</td></tr> ");
            mail.Append("<tr><td style='text-align:center;'>Gracias por su interés en el programa.</td></tr></table>");
            mail.Append("<p><b>NO RESPONDA ESTE CORREO, ES UN ENVÍO AUTOMATIZADO.</b></p></body></html>");
            MailRequest mailRequest = new();
            mailRequest.ToEmail = "gauribe@conavi.gob.mx";
            mailRequest.Subject = "Trámite finalizado";
            mailRequest.Body = mail.ToString();
            bool send = await SendMail(mailRequest);
            if (send)
            {
                var alert = AlertService.ShowAlert(Alerts.Success, "Su solicitud de viaticos ha registrado con el Número de Comisión: " + viaticos.Folio);
                return Ok(alert);
            }
            return BadRequest();
     }
        public async Task<bool> SendMail(MailRequest request)
        {
            try
            {
                await _mailService.SendEmailAsync(request);
                return true;
            }
            catch (Exception ex)
            {

                throw;
            }

        }
        public async Task<bool> SendWelcomeMail(WelcomeRequest request)
        {
            try
            {
                await _mailService.SendWelcomeEmailAsync(request);
                return true;
            }
            catch (Exception ex)
            {

                throw;
            }

        }

    }

}
